create table course
(
    id       bigint auto_increment comment '序号'
        primary key,
    name     varchar(100) not null comment '课程名称',
    teacher  varchar(20)  not null comment '课程老师',
    location varchar(100) not null comment '课程教室',
    week     bigint       not null comment '课程周数',
    day      bigint       not null comment '课程星期数',
    begin    bigint       not null comment '课程开始节数',
    row_span bigint       not null comment '课程共几节'
);

